package fr.eni.filmotheque.controller;import fr.eni.filmotheque.bll.FilmService;import fr.eni.filmotheque.bo.Film;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;//Nous sommes sur une application locale - bean Component@Controllerpublic class FilmController {    // DÃ©pendance    FilmService filmService;    public FilmController(FilmService filmService) {        this.filmService = filmService;    }    @GetMapping("/")    public String index() {        return "index";    }    @GetMapping("/films")    public String displayFilms(Model model) {        model.addAttribute("filmList", filmService.consulterFilms());        return "views_films";    }    @GetMapping("/films/details")    public String displayFilmDetails(@RequestParam(name = "id") long id, Model model) {        model.addAttribute("film", filmService.consulterFilmParId(id));        return "views_film_details";    }    @GetMapping("/films/add")    public String addFilm(Model model) {        model.addAttribute("film", new Film());        return "add_films";    }    @PostMapping("/films/create")    public String createFilm(@ModelAttribute("film") Film film) {        filmService.creerFilm(film);        return "redirect:/films/details";    }}